	def line(self):
		""" Dark Green Horizontal Rule """
		return self.ESC+"32m-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-"+self.ESC+"0m\r\n"

	def blueline(self): 
		""" Dark Blue Horizontal Rule """
		return self.ESC+"34m-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-"+self.ESC+"0m\r\n"
		
	def cntransi(self, text):
		cleantext = re.sub("\x1b\[\d+m", "", text)
		col = 39 - (len(cleantext) / 2)
		ittr = 0
		retval = ""
		while ( ittr < col ):
			retval += " "
			ittr += 1
		return retval + text
		

